import React, { useState} from 'react';
import Task from './task/Task'
import './Todo.css'
import CreateTask from './creat-task/CreateTask';




const Todo = () => {
    const tasklists = [
        {title: 'Do Leetcode exercises' , completed: true},
        {title: 'Take Shower', completed: true},
        {title: 'Have breakfast', completed: true},
        {title: 'Go to office', completed: false}
    ]
    const [tasks, setTask] = useState(tasklists);
    const addTask = (title) => {
         const newTasks = [...tasks, {title: title, completed: false} ];
         setTask(newTasks);
    }

    return (
        <div className='task-container'>
            <div className='task-list'>
            {tasks.map((item, index) => (
                <Task 
                 task={item}
                 key={index} 
                />
            ))}
            </div>
            <div className='add-task'>
                <CreateTask addTask={addTask}/>
            </div>
        </div>
    )
}
export default Todo;
